      </div>
    </div>
  </div>
</template>

<script>
import legends from "assets/content/legends";
import Pattern from "@/components/pattern";

export default {
  components: {Pattern},
  data() {
    return {
      setStory: 1
    }
  },
  computed: {
    mapLegends() {
      return legends.map(elem => {
        return {
          id: elem.id,
          name: elem.name,
          price: elem.price,
          address: elem.address
        }
      })
    },
    mapStory() {
      return legends.find(legend => legend.id === this.setStory)
    },
    setImage() {
      return require(`~/assets/images/Legends/Stories/${this.mapStory.image}.jpg`)
    }
  },
  methods: {
    selectTab(id) {
      this.setStory = id
    }
  }

}
</script>

